    const supabase = await createClient()

    const { error } = await supabase
        .from('notes')
        .delete()
        .eq('id', id)

    if (error) {
        console.error('Delete note error:', error)
        return { error: 'Failed to delete note' }
    }

    revalidatePath(`/courses/${courseId}/notes`)
    revalidatePath(`/dashboard`)
    return { success: true }
}

