export type Quiz = {
    id: string
    user_id: string
    course_id: string
    title: string
    description: string | null
    source_type: 'MANUAL' | 'AI_GENERATED' | null
    created_at: string
    updated_at: string
}

export type QuizQuestion = {
    id: string
    quiz_id: string
    question_text: string
    question_type: 'MULTIPLE_CHOICE' | 'SHORT_ANSWER' | null
    options: any // JSONB array of strings
    answer: string
    explanation: string | null
}

export async function getQuizzesByCourseId(courseId: string) {
    const supabase = await createClient()
    const { data, error } = await supabase
        .from('quizzes')
        .select('*')
        .eq('course_id', courseId)
        .order('created_at', { ascending: false })

    if (error) {
        console.error('Error fetching quizzes:', error)
        return []
    }
    return data as Quiz[]
}

export async function getQuizWithQuestions(quizId: string) {
    const supabase = await createClient()

    const { data: quiz, error: quizError } = await supabase
        .from('quizzes')
        .select('*')
        .eq('id', quizId)
        .single()

    if (quizError || !quiz) {
        console.error('Error fetching quiz info:', quizError)
        return null
    }

    const { data: questions, error: qError } = await supabase
        .from('quiz_questions')
        .select('*')
        .eq('quiz_id', quizId)
        .order('id', { ascending: true }) // simplest order

    if (qError) {
        console.error('Error fetching questions:', qError)
        return { quiz: quiz as Quiz, questions: [] }
    }

    return {
        quiz: quiz as Quiz,
        questions: questions as QuizQuestion[]
    }
}

import { createServerClient } from '@supabase/ssr'
import { NextResponse, type NextRequest } from 'next/server'

export async function updateSession(request: NextRequest) {
    let supabaseResponse = NextResponse.next({
        request,
    })

    const supabase = createServerClient(
        process.env.NEXT_PUBLIC_SUPABASE_URL!,
        process.env.NEXT_PUBLIC_SUPABASE_ANON_KEY!,
        {
            cookies: {
                getAll() {
                    return request.cookies.getAll()
                },
                setAll(cookiesToSet) {
                    cookiesToSet.forEach(({ name, value, options }) => request.cookies.set(name, value))
                    supabaseResponse = NextResponse.next({
                        request,
                    })
                    cookiesToSet.forEach(({ name, value, options }) =>
                        supabaseResponse.cookies.set(name, value, options)
                    )
                },
            },
        }
    )

    // IMPORTANT: Avoid writing any logic between createServerClient and
    // supabase.auth.getUser(). A simple mistake could make it very hard to debug
    // issues with users being randomly logged out.

    const {
        data: { user },
    } = await supabase.auth.getUser()

    // Protect all /app routes
    if (
        !user &&
        !request.nextUrl.pathname.startsWith('/login') &&
        !request.nextUrl.pathname.startsWith('/register') &&
        !request.nextUrl.pathname.startsWith('/auth')
    ) {
        // no user, potentially respond by redirecting the user to the login page
        const url = request.nextUrl.clone()
        url.pathname = '/login'
        return NextResponse.redirect(url)
    }

    return supabaseResponse
}

import { createServerClient, type CookieOptions } from '@supabase/ssr'
import { cookies } from 'next/headers'

export async function createClient() {
